import React from 'react'

export const Game = (props) => {
    return (
        <>
            <p>U: {props.userStep}</p>
            <p>C: {props.computerStep}</p>
            <p>{props.message}</p>
            {
                props.message && <input type="button" value='play again' onClick={props.resetOnClick}/>
            }
        </>
    )
}
