import React from 'react'
import {NavLink} from "react-router-dom";
import rock from '../assets/1.jpg'
import scissor from '../assets/2.png'
import paper from '../assets/3.png'
import white from '../assets/bg_picture.png'
import '../App.css'
import useSound from 'use-sound';
import sound from '../assets/salamisound-3834747-alarm-electric-alarm-clock.mp3'

export const Game = (props) => {

    const userStep = () => {
        switch (props.userStep) {
            case 'r':
                return rock
            case 's':
                return scissor
            case 'p':
                return paper

        }
    }

    const compStep = () => {
        switch (props.computerStep) {
            case 'r':
                return rock
            case 's':
                return scissor
            case 'p':
                return paper

        }
    }

    const [play] = useSound(boopSfx);


    return (
        <>
            <div className='play_container1'>
                <div className='ch'>
                    <div className='userStep_text'>You
                        picked {props.userStep === 'r' ? 'Rock' : props.userStep === 's' ? 'Scissor' : 'Paper'}</div>
                    <div className='computerStep_text'>The Computer
                        picked {props.computerStep === 'r' ? 'Rock' : props.computerStep === 's' ? 'Scissor' : 'Paper'}</div>
                </div>
                <div className="loh">
                    <div className='steps'>
                        <div className='userStep_container'>
                        <span>
                            {props.userStep && <img className='img' src={userStep()}/>}
                        </span>
                        </div>
                        <div>
                            <div className='game_message'>{props.message}</div>
                            <div>{
                                props.message &&
                                <NavLink to='/'><input type="button" className="btn btn-success" value='play again'
                                                       onClick={props.resetOnClick}/></NavLink>
                            }</div>
                        </div>
                        <div className='computerStep_container'>
                        <span>
                            {<img className='img' src={!props.computerStep ? white : compStep()}/>}
                        </span>
                        </div>
                    </div>
                </div>
            </div>
            <div className="lolo">
                {/*{props.message &&
                <div className="lol alert alert-primary" role="alert">
                    {props.message}
                </div>
                }*/}
                <button onClick={play}>Boop!</button>;
            </div>
            {/*<p>{props.message}</p>*/}
            {/*{
                props.message &&
                <NavLink to='/'><input type="button" className="btn btn-success" value='play again'
                                       onClick={props.resetOnClick}/></NavLink>
            }*/}
        </>
    )
}
