import React, {useState} from 'react'
import {data} from "../data/data";


export class Main extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userStep: '',
            computerStep: '',
            message: '',
            count: 0
        }
    }

    getCopmuterStep = (data) => {
        let rand = Math.floor(Math.random() * 3)
        setTimeout(() => {
            this.setState({computerStep: data[rand]})
        }, 2000)
    }

    game = () => {
        const {userStep, computerStep} = this.state
        let combine = userStep + computerStep

        switch (combine) {
            case 'rr':
            case 'ss':
            case 'pp':
                return 'draw'
                break;
            case 'rs':
            case 'sp':
            case 'pr':
                this.setState((prevState) => {
                    return {
                        count: prevState.count + 1
                    }
                })
                return 'You win!'
                break;
            case 'sr':
            case 'ps':
            case 'rp':
                return 'You lose!'
                break;
        }

        /*if (combine === 'rs' || 'sp' || 'pr') {
            this.setState((prevState) => {
                return {
                    count: prevState.count + 1
                }
            })
        } else if (combine === 'sr' || 'ps' || 'rp') {
            this.setState((prevState) => {
                return {
                    count: prevState.count - 1
                }
            })
        }*/
    }


    userStepOnClick = (e) => {
        this.setState({userStep: e.target.name})
        this.getCopmuterStep(data)
        setTimeout(() => {
            this.setState({message: this.game()})
        }, 2000)
    }

    resetOnClick = () => {
        this.setState({userStep: '', computerStep: '', message: ''})
    }




    render() {
        return (
            <div>
                <input type="button" name='r' value='Rock' onClick={this.userStepOnClick}/>
                <input type="button" name='s' value='Scissor' onClick={this.userStepOnClick}/>
                <input type="button" name='p' value='Paper' onClick={this.userStepOnClick}/>
                <p>{this.state.count}</p>
                <p>U: {this.state.userStep}</p>
                <p>C: {this.state.computerStep}</p>
                <p>{this.state.message}</p>
                {
                    this.state.message && <input type="button" value='play again' onClick={this.resetOnClick}/>
                }
            </div>
        )
    }
}


