import React, {useState} from 'react'
import {data} from "../data/data";
import {Game} from "./Play";
import {NavLink} from "react-router-dom";


export class Main extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userStep: '',
            computerStep: '',
            message: '',
            count: 0
        }
    }

    getCopmuterStep = (data) => {
        let rand = Math.floor(Math.random() * 3)
        setTimeout(() => {
            this.setState({computerStep: data[rand]})
        }, 2000)
    }

    game = () => {
        const {userStep, computerStep} = this.state
        let combine = userStep + computerStep

        switch (combine) {
            case 'rr':
            case 'ss':
            case 'pp':
                return 'draw'
                break;
            case 'rs':
            case 'sp':
            case 'pr':
                this.setState((prevState) => {
                    return {
                        count: prevState.count + 1
                    }
                })
                return 'You win!'
                break;
            case 'sr':
            case 'ps':
            case 'rp':
                this.setState((prevState) => {
                    return {
                        count: prevState.count - 1
                    }
                })
                return 'You lose!'
                break;
        }
    }


    userStepOnClick = (e) => {
        this.setState({userStep: e.target.name})
        this.getCopmuterStep(data)
        setTimeout(() => {
            this.setState({message: this.game()})
        }, 2000)
    }

    resetOnClick = () => {
        this.setState({userStep: '', computerStep: '', message: ''})
    }


    render() {
        const {count, userStep, computerStep, message} = this.state
        return (
            <div>
                <NavLink to='/game'>
                    <input type="button" name='r' value='Rock' onClick={this.userStepOnClick}/>
                    <input type="button" name='s' value='Scissor' onClick={this.userStepOnClick}/>
                    <input type="button" name='p' value='Paper' onClick={this.userStepOnClick}/>
                </NavLink>
                <Game userStepOnClick={this.userStepOnClick}
                      resetOnClick={this.resetOnClick}
                      count={count}
                      userStep={userStep}
                      computerStep={computerStep}
                      message={message}
                />
            </div>
        )
    }
}
