import React, {useState} from 'react'
import {data} from "../data/data";


export const Main = () => {
    const [userStep, setUserStep] = useState('')
    const userStepOnChange = (e) => {
        setUserStep(e.target.name)

    }

    return (
        <div>
            <input type="button" name='r' value='Rock' onClick={userStepOnChange}/>
            <input type="button" name='s' value='Scissor' onClick={userStepOnChange}/>
            <input type="button" name='p' value='Paper' onClick={userStepOnChange}/>
            <p>{userStep}</p>
        </div>
    )
}
