import React, {useState} from 'react'
import {NavLink} from "react-router-dom";
import {Main} from "./Main";
import useSound from "use-sound";
import sound from "../assets/a30aa53e7ecd230.mp3";


export const Menu = (props) => {
    const [nickname, setNick] = useState('')

    const setName = (e) => {
        let name = e.target.value
        setNick(name)
        props.setNickname(name)
    }

    const setNameOnClick = () => {
        setNick('')
    }


    return (
        <div className='menu-container'>

            <div>
                <div className="input-group mb-3">
                    <form action="">
                        <input type="text" className="form-control" placeholder="Recipient's username"
                               aria-label="Recipient's username" aria-describedby="button-addon2" value={nickname}
                               onChange={setName}/>
                        <button className="btn btn-success" type="button" id="button-addon2"
                                onClick={setNameOnClick}>send
                        </button>
                    </form>
                </div>
                <NavLink to='/main'>
                    <div onClick={() => {
                        localStorage.score = 0
                    }} className='menu-item'>Start
                    </div>
                </NavLink>
                <div className='menu-item'>Options</div>
                <div className='menu-item'>Rules</div>
            </div>

        </div>
    )
}
