import React from 'react'
import {NavLink} from "react-router-dom";

export const Game = (props) => {
    return (
        <>
            <span style={{marginRight: 5}}>U: {props.userStep}</span>
            <span>C: {props.computerStep}</span>
            <p>{props.message}</p>
            {
                props.message &&
                <NavLink to='/'><input type="button" value='play again' onClick={props.resetOnClick}/></NavLink>
            }
        </>
    )
}
