import React from 'react'
import {NavLink} from "react-router-dom";
import {Main} from "./Main";
import useSound from "use-sound";
import sound from "../assets/a30aa53e7ecd230.mp3";


export const Menu = (props) => {
    const setName = (e) => {
       props.setNickname(e.target.value)
        e.target.value = ''
    }
    return (
        <div className='menu-container'>

            <div>
                <form></form>
                <input type="text" value={props.nickname} onChange={setName}/>

                <NavLink to='/main'>
                    <div onClick={()=>{localStorage.score = 0}} className='menu-item'>Start</div>
                </NavLink>
                <div className='menu-item'>Options</div>
                <div className='menu-item'>Rules</div>
            </div>

        </div>
    )
}
